import { useState } from "react";

export default function Form() {
  const [firstName, setName] = useState("");
  const [listName, setlistName] = useState("");
  const [password, setPassword] = useState("");
  const [date, setDate] = useState();



  const pass = false ;
  function handelrSubmit(e) {
    e.preventDefault();

    setName("");
    setlistName("");
    setPassword("");
    setDate("");
  }
  return (
    <>
      <form
        className=" text-xl  py-10 px-10     "
        onSubmit={(e) => handelrSubmit(e)}
      >
        <div className=" pb-10 grid  grid-cols-2 grid-row-2  gap-8 ">
          <div className=" flex flex-col  ">
            <label
              className="block  text-lg font-bold mb-2"
              htmlFor="firstName"
            >
              Your Fist Name
            </label>
            <input
              required
              value={firstName}
              className=" w-full placeholder:text-[#eeeeee] placeholder:font-bold px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:border-[#adcbe3] bg-[#faf9f96c] text-white   "
              id="firstName"
              type="text"
              placeholder="Your Fist Name"
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className=" flex flex-col   ">
            <label className="block  text-lg font-bold mb-2" htmlFor="listName">
              Your List Name
            </label>
            <input
              required
              value={listName}
              placeholder="Your Fist Name"
              className=" w-full placeholder:text-[#eeeeee] placeholder:font-bold px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:border-[#adcbe3] bg-[#faf9f96c] text-white   "
              id="listName"
              type="text"
              onChange={(e) => setlistName(e.target.value)}
            />
          </div>
          <div className=" flex flex-col relative  ">
            <label className="block  text-lg font-bold mb-2" htmlFor="password">
              Password
            </label>
            <input
              className=" w-full placeholder:text-[#eeeeee] placeholder:font-bold px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:border-[#adcbe3] bg-[#faf9f96c] text-white   "
              id="password"
              type="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
              required
              value={password}
              
            />
            {pass   && (
              <div className="  text-md text-red-500   absolute -bottom-6  font-bold">
                Your password should be more than 5 characters
              </div>
            )}
          </div>
          <div className=" flex flex-col  ">
            <label className="block  text-lg font-bold mb-2" htmlFor="Date">
              Date
            </label>
            <input
              className=" w-full placeholder:text-[#eeeeee] placeholder:font-bold px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:border-[#adcbe3] bg-[#faf9f96c] text-white   "
              id="Date"
              type="date"
              required
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
          </div>
        </div>
        <button
          onClick={(e) => {
            if (password.length < 5) {
              e.preventDefault();
              
            }
          }}
          className=" font-bold shadow-md py-3 px-8 bg-[#162849] rounded-md"
        >
          Log In && sent
        </button>
      </form>
    </>
  );
}
